    constructor(){
        super()
        this.state = {
            posts: []

        }
    }

    getAllPost(){
        return posts.map((el) =>{
            return(
                <div>
                    <h1>{el.title}</h1>
                    <p>{el.content}</p>
                </div>
            )
        })
    }

    render(){
        console.log(posts)
        return(
            <><img 
            src ="https://scontent-bos3-1.xx.fbcdn.net/v/t31.0-8/p720x720/22829296_10155350291058778_764837939569655473_o.jpg?_nc_cat=100&_nc_ohc=NVQIGAHIkJQAQnKgbBk_ph59xc6rNFasaB0CA1zrQmZ0WciKNVAjCyDFg&_nc_ht=scontent-bos3-1.xx&oh=1d722ed3e29e5614bb50cc058fd67868&oe=5E779D60" 
            alt = "my pic"
            width = "300px"
            height = "300px"
            />

                <p> I am a programmer who loves to program. I am currently making websites and I love to make Video Games.
                    My time in pursuit is fun. And has been very engaging.
                </p>
                {this.getAllPost()}
            </>
        )

    }

}





export default Blog
