import React, { Component } from 'react';
import PostsAPI from '../PostApi';

class Post extends Component {
  constructor() {
    super();
    this.state = {
      post: {}
    }
  }

  componentDidMount() {
    // let data = PostsAPI.getOne(this.props.match.params.id);
    let data = PostsAPI.getOne("1");
    this.setState({
      post: data
    })
  }

  render() {
    let { post } = this.state

    return (
      <div>
        <h1>{post.title}</h1>
        <p>{post.content}</p>
      </div>
    )
  }
}

export default Post;
